CREATE FUNCTION create_key_for_tunnel(tunnel_type, uuid, key_type, text) RETURNS UUID AS '
    DECLARE
        tunnel_type ALIAS FOR $1;
        tunnel_id   ALIAS FOR $2;
        key_type ALIAS FOR $3;
        contents ALIAS FOR $4;

        key_id  UUID;

    BEGIN

    INSERT INTO keys (key_type, contents) VALUES (key_type, contents) RETURNING id INTO key_id;
    INSERT INTO key_authorizations (key_id, tunnel_type, tunnel_id) VALUES (key_id, tunnel_type, tunnel_id);

    RETURN key_id;
    END;
' LANGUAGE 'plpgsql';

-- set up normal tunnels
INSERT INTO tunnels (ssh_host, ssh_port, service_host, service_port, enabled) VALUES
    ('localhost', 23456, 'localhost', 6969, false)
;

SELECT create_key_for_tunnel(tunnel_type('normal'), (SELECT id FROM tunnels WHERE ssh_port=23456), key_type('private'), convert_from(decode('', 'base64'), 'UTF8'));